```cpp
#include <string>
#include <unordered_map>
#include <vector>

std::string markdownToHtml(std::string& markdown) {
    std::unordered_map<std::string, std::string> markdownToHtmlConv;
    markdownToHtmlConv["**"] = "<strong>";
    markdownToHtmlConv["*"] = "<em>";
    markdownToHtmlConv["```"] = "<pre>";
    markdownToHtmlConv["```"] = "</pre>";
    markdownToHtmlConv["["] = "<span>";
    markdownToHtmlConv["]"] = "</span>"; 

    std::string html;
    size_t startCodeBlock = markdown.find("```");
    size_t startBold = markdown.find("**");
    size_t startEm = markdown.find("*");
    size_t startLink = markdown.find("[");

    while (startCodeBlock != std::string::npos || startBold != std::string::npos || startEm != std::string::npos || startLink != std::string::npos) {
        // Add current text without markdown syntax
        html += markdown.substr(0, startCodeBlock);

        if (startCodeBlock != std::string::npos) {
            // Add code block syntax as html
            html += markdownToHtmlConv["```"];
            startCodeBlock = markdown.find("```", startCodeBlock + 3);
            html += markdown.substr(startCodeBlock, markdown.find("```", startCodeBlock) - startCodeBlock) + "\n";
            startCodeBlock += 3;
            html += markdownToHtmlConv["```"];
        }

        if (startBold != std::string::npos) {
            // Add strong syntax as html
            html += markdownToHtmlConv["**"];
            startBold = markdown.find("**", startBold + 2);
            html += markdown.substr(startBold, markdown.find("**", startBold) - startBold) + "\n";
            startBold += 2;
            html += markdownToHtmlConv["**"];
        }

        if (startEm != std::string::npos) {
            // Add em syntax as html
            html += markdownToHtmlConv["*"];
            startEm = markdown.find("*", startEm + 1);
            html += markdown.substr(startEm, markdown.find("*", startEm) - startEm) + "\n";
            startEm += 1;
            html += markdownToHtmlConv["*"];
        }

        if (startLink != std::string::npos) {
            // Add link syntax as html
            html += markdownToHtmlConv["["];
            startLink = markdown.find("[", startLink + 1);
            html += markdown.substr(startLink, markdown.find("[", startLink) - startLink) + "\n";
            startLink += 1;
            html += markdownToHtmlConv["]"] + "\n";
        }

        // Move markdown string pointer to the end of the current syntax element
        markdown = markdown.substr(startCodeBlock != std::string::npos ? startCodeBlock : startBold != std::string::npos ? startBold : startEm != std::string::npos ? startEm : startLink + 1);
    }

    // Add remaining text (if any)
    html += markdown;

    return html;
}
```

